    and user_num=#{userNum}
    and act_title=#{actTitle}
    and act_desc=#{actDesc}
    and start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
    and end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
