mapper.select:
select




from activity_set activitySet

    and acs_num=#{acsNum}
    and act_num=#{actNum}
    and user_num=#{userNum}
    and status=#{status}
    and start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
    and end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')

select count(*)
from activity_set

    and acs_num=#{acsNum}
    and act_num=#{actNum}
    and user_num=#{userNum}
    and status=#{status}
    and start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
    and end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')

mapper.insert:
insert into activity_set (

        , acs_num
        , act_num
        , user_num
        , status
        , start_date
        , end_date

) values (

        , #{acsNum}
        , #{actNum}
        , #{userNum}
        , #{status}
        , to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
        , to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')

)

mapper.delete:
delete from activity_set

    and acs_num=#{acsNum}
    and act_num=#{actNum}
    and user_num=#{userNum}
    and status=#{status}
    and start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
    and end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')

mapper.update:
update activity_set


    , act_num=#{actNum}
    , user_num=#{userNum}

        , status=#{status}
        , status=null


        , start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
        , start_date=null


        , end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
        , end_date=null


where
    acs_num=#{acsNum}

update activity_set

                    , act_num=#{actNum}

                    , user_num=#{userNum}

, status=#{status}
, status=null

, start_date=to_timestamp(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
, start_date=null

, end_date=to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
, end_date=null


where
    acs_num=#{acsNum}
