mapper.select:
select




from app_user appUser

    and user_num=#{userNum}
    and user_name=#{userName}
    and user_gender=#{userGender}
    and user_mobile=#{userMobile}
    and user_email=#{userEmail}
    and user_pwd=#{userPwd}
    and user_line_name=#{userLineName}
    and user_line_id=#{userLineId}
    and user_line_pic=#{userLinePic}

select count(*)
from app_user

    and user_num=#{userNum}
    and user_name=#{userName}
    and user_gender=#{userGender}
    and user_mobile=#{userMobile}
    and user_email=#{userEmail}
    and user_pwd=#{userPwd}
    and user_line_name=#{userLineName}
    and user_line_id=#{userLineId}
    and user_line_pic=#{userLinePic}

mapper.insert:
insert into app_user (

        , user_num
        , user_name
        , user_gender
        , user_mobile
        , user_email
        , user_pwd
        , user_line_name
        , user_line_id
        , user_line_pic

) values (

        , #{userNum}
        , #{userName}
        , #{userGender}
        , #{userMobile}
        , #{userEmail}
        , #{userPwd}
        , #{userLineName}
        , #{userLineId}
        , #{userLinePic}

)

mapper.delete:
delete from app_user

    and user_num=#{userNum}
    and user_name=#{userName}
    and user_gender=#{userGender}
    and user_mobile=#{userMobile}
    and user_email=#{userEmail}
    and user_pwd=#{userPwd}
    and user_line_name=#{userLineName}
    and user_line_id=#{userLineId}
    and user_line_pic=#{userLinePic}

mapper.update:
