mapper.select:
select




from app_user_member_status appUserMemberStatus

    and aums_num=#{aumsNum}
    and user_num=#{userNum}
    and user_status=#{userStatus}
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and follow_date=to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_date=to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_time=#{unFollowTime}

select count(*)
from app_user_member_status

    and aums_num=#{aumsNum}
    and user_num=#{userNum}
    and user_status=#{userStatus}
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and follow_date=to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_date=to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_time=#{unFollowTime}

mapper.insert:
insert into app_user_member_status (

    , aums_num
    , user_num
    , user_status
    , create_date
    , follow_date
    , un_follow_date
    , un_follow_time

) values (

    , #{aumsNum}
    , #{userNum}
    , #{userStatus}
    , to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    , to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
    , to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
    , #{unFollowTime}

)

mapper.delete:
delete from app_user_member_status

    and aums_num=#{aumsNum}
    and user_num=#{userNum}
    and user_status=#{userStatus}
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and follow_date=to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_date=to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
    and un_follow_time=#{unFollowTime}

mapper.update:
update app_user_member_status


    , user_num=#{userNum}

        , user_status=#{userStatus}
        , user_status=null


        , create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
        , create_date=null


        , follow_date=to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
        , follow_date=null


        , un_follow_date=to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
        , un_follow_date=null

    , un_follow_time=#{unFollowTime}

where
aums_num=#{aumsNum}

update app_user_member_status

    , user_num=#{userNum}

    , user_status=#{userStatus}
    , user_status=null

    , follow_date=to_timestamp(#{followDate}, 'YYYY-MM-DD HH24:MI:SS')
    , follow_date=null

    , un_follow_date=to_timestamp(#{unFollowDate}, 'YYYY-MM-DD HH24:MI:SS')
    , un_follow_date=null

    , un_follow_time=#{unFollowTime}


where
aums_num=#{aumsNum}
