mapper.select:
select




from payment_record paymentRecord

    and payment_num=#{paymentNum}
    and user_num=#{userNum}
    and payment_title=#{paymentTitle}
    and payment_desc=#{paymentDesc}
    and payment_cost=#{paymentCost}
    and record_type=#{recordType}
    and record_date=to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and update_date=to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')

select count(*)
from payment_record

    and payment_num=#{paymentNum}
    and user_num=#{userNum}
    and payment_title=#{paymentTitle}
    and payment_desc=#{paymentDesc}
    and payment_cost=#{paymentCost}
    and record_type=#{recordType}
    and record_date=to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and update_date=to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')

mapper.insert:
insert into payment_record (

    , payment_num
    , user_num
    , payment_title
    , payment_desc
    , payment_cost
    , record_type
    , record_date
    , create_date
    , update_date

) values (

    , #{paymentNum}
    , #{userNum}
    , #{paymentTitle}
    , #{paymentDesc}
    , #{paymentCost}
    , #{recordType}
    , to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
    , to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    , to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')

)

mapper.delete:
delete from payment_record

    and payment_num=#{paymentNum}
    and user_num=#{userNum}
    and payment_title=#{paymentTitle}
    and payment_desc=#{paymentDesc}
    and payment_cost=#{paymentCost}
    and record_type=#{recordType}
    and record_date=to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
    and create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
    and update_date=to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')

mapper.update:
update payment_record


    , user_num=#{userNum}

        , payment_title=#{paymentTitle}
        , payment_title=null


        , payment_desc=#{paymentDesc}
        , payment_desc=null

    , payment_cost=#{paymentCost}

        , record_type=#{recordType}
        , record_type=null


        , record_date=to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
        , record_date=null


        , create_date=to_timestamp(#{createDate}, 'YYYY-MM-DD HH24:MI:SS')
        , create_date=null


        , update_date=to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')
        , update_date=null


where
payment_num=#{paymentNum}

update payment_record

    , user_num=#{userNum}

    , payment_title=#{paymentTitle}
    , payment_title=null

    , payment_desc=#{paymentDesc}
    , payment_desc=null

    , payment_cost=#{paymentCost}

    , record_type=#{recordType}
    , record_type=null

    , record_date=to_timestamp(#{recordDate}, 'YYYY-MM-DD HH24:MI:SS')
    , record_date=null

    , update_date=to_timestamp(#{updateDate}, 'YYYY-MM-DD HH24:MI:SS')
    , update_date=null


where
payment_num=#{paymentNum}
